import Foundation

struct OnboardingItem: Identifiable {
    let id = UUID()
    let title: String
    let description: String
    let imageName: String
}

import Foundation
import SwiftUI

@MainActor
class GoalsViewModel: ObservableObject {
    @Published var goals: [Goal] = []
    @Published var showSuccessNotification = false

    private let goalsKey = "savedGoals"

    init() {
        loadGoals()
    }

    func addGoal(_ goal: Goal) {
        goals.append(goal)
        saveGoals()
        showSuccessNotification = true

        // Auto-hide notification after 2 seconds
        Task {
            try? await Task.sleep(nanoseconds: 2_000_000_000)
            showSuccessNotification = false
        }
    }

    func updateGoal(_ goal: Goal) {
        if let index = goals.firstIndex(where: { $0.id == goal.id }) {
            goals[index] = goal
            saveGoals()
        }
    }

    func deleteGoal(_ goal: Goal) {
        goals.removeAll { $0.id == goal.id }
        saveGoals()
    }

    func toggleTaskCompletion(goalId: UUID, taskId: UUID) {
        if let goalIndex = goals.firstIndex(where: { $0.id == goalId }),
           let taskIndex = goals[goalIndex].tasks.firstIndex(where: { $0.id == taskId }) {
            goals[goalIndex].tasks[taskIndex].isCompleted.toggle()

            // Update progress
            let completedTasks = goals[goalIndex].tasks.filter { $0.isCompleted }.count
            let totalTasks = goals[goalIndex].tasks.count
            goals[goalIndex].progress = totalTasks > 0 ? Double(completedTasks) / Double(totalTasks) : 0.0

            saveGoals()
        }
    }

    private func saveGoals() {
        if let encoded = try? JSONEncoder().encode(goals) {
            UserDefaults.standard.set(encoded, forKey: goalsKey)
        }
    }

    private func loadGoals() {
        if let data = UserDefaults.standard.data(forKey: goalsKey),
           let decoded = try? JSONDecoder().decode([Goal].self, from: data) {
            goals = decoded
        }
    }
}

import Foundation

class OnboardingViewModel: ObservableObject {
    @Published var currentPage = 0

    let onboardingItems = [
        OnboardingItem(
            title: "Track Your Goals",
            description: "Create and organize your personal goals with deadlines and progress tracking",
            imageName: "chart.bar.fill"
        ),
        OnboardingItem(
            title: "Break Down Tasks",
            description: "Split your goals into manageable tasks and track completion",
            imageName: "checklist"
        ),
        OnboardingItem(
            title: "Stay Organized",
            description: "Monitor your progress and celebrate achievements as you reach your goals",
            imageName: "star.fill"
        )
    ]

    var isLastPage: Bool {
        currentPage == onboardingItems.count - 1
    }

    func nextPage() {
        if currentPage < onboardingItems.count - 1 {
            currentPage += 1
        }
    }

    func markOnboardingComplete() {
        UserDefaults.standard.set(true, forKey: "hasCompletedOnboarding")
    }

    static func hasCompletedOnboarding() -> Bool {
        UserDefaults.standard.bool(forKey: "hasCompletedOnboarding")
    }
}

import SwiftUI

struct MainTabView: View {
    @AppStorage("isDarkMode") private var isDarkMode = false
    @StateObject private var goalsViewModel = GoalsViewModel()

    var body: some View {
        TabView {
            NavigationStack {
                GoalsListView()
            }
            .tabItem {
                Label("Goals", systemImage: "target")
            }

            NavigationStack {
                AddGoalView()
            }
            .tabItem {
                Label("Add Goal", systemImage: "plus.circle.fill")
            }

            NavigationStack {
                SettingsView()
            }
            .tabItem {
                Label("Settings", systemImage: "gear")
            }
        }
        .environmentObject(goalsViewModel)
        .preferredColorScheme(isDarkMode ? .dark : .light)
    }
}

import SwiftUI

struct GoalsListView: View {
    @EnvironmentObject private var viewModel: GoalsViewModel

    var body: some View {
        ScrollView {
            LazyVStack(spacing: 16) {
                ForEach(viewModel.goals) { goal in
                    NavigationLink(destination: GoalDetailView(goal: goal)) {
                        GoalCardView(goal: goal)
                    }
                }
            }
            .padding()
        }
        .navigationTitle("My Goals")
        .overlay {
            if viewModel.goals.isEmpty {
                ContentUnavailableView(
                    "No Goals Yet",
                    systemImage: "target",
                    description: Text("Tap the + tab to add your first goal")
                )
            }
        }
        .overlay(alignment: .top) {
            if viewModel.showSuccessNotification {
                NotificationBanner(text: "Goal added successfully!")
                    .transition(.move(edge: .top).combined(with: .opacity))
            }
        }
        .animation(.spring(response: 0.3), value: viewModel.showSuccessNotification)
    }
}

struct GoalCardView: View {
    let goal: Goal

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            HStack {
                Text(goal.title)
                    .font(.headline)
                Spacer()
                Image(systemName: goal.isCompleted ? "checkmark.circle.fill" : "circle")
                    .foregroundColor(goal.isCompleted ? .green : .gray)
            }

            Text(goal.description)
                .font(.subheadline)
                .foregroundColor(.secondary)
                .lineLimit(2)

            ProgressView(value: goal.progress)
                .tint(.green)

            HStack {
                Image(systemName: "calendar")
                    .foregroundColor(.secondary)
                Text(goal.deadline, style: .date)
                    .font(.caption)
                    .foregroundColor(.secondary)

                Spacer()

                Text("\(Int(goal.progress * 100))%")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }
        }
        .padding()
        .background(Color(.systemBackground))
        .cornerRadius(12)
        .shadow(radius: 2)
    }
}

struct NotificationBanner: View {
    let text: String

    var body: some View {
        Text(text)
            .font(.subheadline.bold())
            .foregroundColor(.white)
            .padding()
            .background(Color.green)
            .cornerRadius(8)
            .shadow(radius: 2)
            .padding(.top)
    }
}

import SwiftUI

struct AddGoalView: View {
    @EnvironmentObject private var viewModel: GoalsViewModel
    @Environment(\.dismiss) private var dismiss
    @Environment(\.tabSelection) private var tabSelection

    @State private var title = ""
    @State private var description = ""
    @State private var deadline = Date()
    @State private var tasks: [Goal.Task] = []
    @State private var newTask = ""

    var body: some View {
        Form {
            Section("Goal Details") {
                TextField("Title", text: $title)
                TextField("Description", text: $description, axis: .vertical)
                    .lineLimit(3...6)
                DatePicker("Deadline", selection: $deadline, in: Date()...)
            }

            Section("Tasks") {
                ForEach(tasks) { task in
                    Text(task.title)
                }
                .onDelete { indexSet in
                    tasks.remove(atOffsets: indexSet)
                }

                HStack {
                    TextField("Add task", text: $newTask)
                    Button("Add") {
                        if !newTask.isEmpty {
                            tasks.append(Goal.Task(title: newTask))
                            newTask = ""
                        }
                    }
                    .disabled(newTask.isEmpty)
                }
            }
        }
        .navigationTitle("Add Goal")
        .toolbar {
            ToolbarItem(placement: .topBarTrailing) {
                Button("Save") {
                    let goal = Goal(
                        title: title,
                        description: description,
                        deadline: deadline,
                        tasks: tasks
                    )
                    viewModel.addGoal(goal)

                    // Reset form
                    title = ""
                    description = ""
                    deadline = Date()
                    tasks = []
                    newTask = ""

                    // Switch to first tab
                    tabSelection = 0
                }
                .disabled(title.isEmpty)
            }
        }
    }
}

import SwiftUI

struct GoalDetailView: View {
    @EnvironmentObject private var viewModel: GoalsViewModel
    @Environment(\.dismiss) private var dismiss

    let goal: Goal
    @State private var isEditing = false
    @State private var showingDeleteAlert = false

    var body: some View {
        ScrollView {
            VStack(alignment: .leading, spacing: 20) {
                // Header
                VStack(alignment: .leading, spacing: 8) {
                    Text(goal.title)
                        .font(.title.bold())
                    Text(goal.description)
                        .foregroundColor(.secondary)
                }

                // Progress
                VStack(alignment: .leading, spacing: 8) {
                    Text("Progress")
                        .font(.headline)
                    ProgressView(value: goal.progress)
                        .tint(.green)
                    Text("\(Int(goal.progress * 100))% Complete")
                        .foregroundColor(.secondary)
                }

                // Deadline
                VStack(alignment: .leading, spacing: 8) {
                    Text("Deadline")
                        .font(.headline)
                    Text(goal.deadline, style: .date)
                        .foregroundColor(.secondary)
                }

                // Tasks
                VStack(alignment: .leading, spacing: 12) {
                    Text("Tasks")
                        .font(.headline)

                    ForEach(goal.tasks) { task in
                        Button {
                            viewModel.toggleTaskCompletion(goalId: goal.id, taskId: task.id)
                        } label: {
                            HStack {
                                Image(systemName: task.isCompleted ? "checkmark.circle.fill" : "circle")
                                    .foregroundColor(task.isCompleted ? .green : .gray)
                                Text(task.title)
                                    .strikethrough(task.isCompleted)
                                Spacer()
                            }
                        }
                        .buttonStyle(.plain)
                    }
                }
            }
            .padding()
        }
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .topBarTrailing) {
                Menu {
                    Button {
                        isEditing = true
                    } label: {
                        Label("Edit", systemImage: "pencil")
                    }

                    Button(role: .destructive) {
                        showingDeleteAlert = true
                    } label: {
                        Label("Delete", systemImage: "trash")
                    }

                    Button {
                        let items = [goal.title, goal.description]
                        let av = UIActivityViewController(activityItems: items, applicationActivities: nil)
                        UIApplication.shared.windows.first?.rootViewController?.present(av, animated: true)
                    } label: {
                        Label("Share", systemImage: "square.and.arrow.up")
                    }
                } label: {
                    Image(systemName: "ellipsis.circle")
                }
            }
        }
        .sheet(isPresented: $isEditing) {
            NavigationStack {
                EditGoalView(goal: goal)
            }
        }
        .alert("Delete Goal", isPresented: $showingDeleteAlert) {
            Button("Cancel", role: .cancel) { }
            Button("Delete", role: .destructive) {
                viewModel.deleteGoal(goal)
                dismiss()
            }
        } message: {
            Text("Are you sure you want to delete this goal? This action cannot be undone.")
        }
    }
}

struct EditGoalView: View {
    @EnvironmentObject private var viewModel: GoalsViewModel
    @Environment(\.dismiss) private var dismiss

    let goal: Goal
    @State private var title: String
    @State private var description: String
    @State private var deadline: Date
    @State private var tasks: [Goal.Task]
    @State private var newTask = ""

    init(goal: Goal) {
        self.goal = goal
        _title = State(initialValue: goal.title)
        _description = State(initialValue: goal.description)
        _deadline = State(initialValue: goal.deadline)
        _tasks = State(initialValue: goal.tasks)
    }

    var body: some View {
        Form {
            Section("Goal Details") {
                TextField("Title", text: $title)
                TextField("Description", text: $description, axis: .vertical)
                    .lineLimit(3...6)
                DatePicker("Deadline", selection: $deadline, in: Date()...)
            }

            Section("Tasks") {
                ForEach(tasks) { task in
                    Text(task.title)
                }
                .onDelete { indexSet in
                    tasks.remove(atOffsets: indexSet)
                }

                HStack {
                    TextField("Add task", text: $newTask)
                    Button("Add") {
                        if !newTask.isEmpty {
                            tasks.append(Goal.Task(title: newTask))
                            newTask = ""
                        }
                    }
                    .disabled(newTask.isEmpty)
                }
            }
        }
        .navigationTitle("Edit Goal")
        .navigationBarTitleDisplayMode(.inline)
        .toolbar {
            ToolbarItem(placement: .topBarLeading) {
                Button("Cancel") {
                    dismiss()
                }
            }

            ToolbarItem(placement: .topBarTrailing) {
                Button("Save") {
                    let updatedGoal = Goal(
                        id: goal.id,
                        title: title,
                        description: description,
                        deadline: deadline,
                        progress: goal.progress,
                        tasks: tasks,
                        isCompleted: goal.isCompleted
                    )
                    viewModel.updateGoal(updatedGoal)
                    dismiss()
                }
                .disabled(title.isEmpty)
            }
        }
    }
}

